import   React from "react"
import  Lis from "./Lis"
import  Userselect  from "./Userselect"
import  OrderList  from "./Orderlist"
import   Fenye  from "./Fenye"
export  default  class Userlist extends  React.Component{
    constructor(props){
          super(props)
          this.state={
                userlist:[],
                timeStart:"",
                timeEnd:""
          }
    }
    componentDidMount(){
          fetch("http://pre.zhushang.net/Supplychain/getDataForHavePost",{
                 method:"post",
                 mode:"cors",
                 headers:{
                     "Content-type": "application/x-www-form-urlencoded"
                 },
                 body : "type=1&page=1&num=20"

          }).then (response =>{
                 return   response.json()
          }).then( (responseText)=>{
                  this.setState({userlist:responseText})
          }  )
      }

    //  输入框子组件的日期1传递给父组件
    onchangeValue1(e){
       this.setState({timeStart:e.target.value})
    }
    // //  输入框子组件的日期2传递给父组件
    onchangeValue2(e){
       this.setState({timeEnd:e.target.value})   
    }
    // 按钮查询功能
    select(){
       const time1=  new Date(this.setState.timeStart).getTime()
       const time2=  new Date(this.setState.timeEnd).getTime()
       console.log(time1,time2)
    }

    render(){  
             console.log(this.state.timeEnd,this.state.timeStart)
             const arr1=this.state.userlist.slice(0,8)
             const arr2=this.state.userlist.slice(10)
             const arr=[...arr1,...arr2]
             const  newarr=arr.map((elem ,i)=> {
                   return   <Lis  key={i} {...elem}></Lis>
             })
            return  <div id="tab"> 
                      <div id="sale">
                            <Userselect change1={this.onchangeValue1.bind(this)} change2={this.onchangeValue2.bind(this)}
                               value1={this.state.timeStart}   value2={this.state.timeEnd}/>
                               <div id="buttonclick">
                                  <button id="select"  onClick={this.select}>查询</button>
                                  <button id="asyc">同步</button>
                                  <span className="text"> 交易上次手动同步时间:  <span id="time"> 2019-09-25 10:09:36 </span></span>
                               </div>
                            <OrderList/>
                            <div>{newarr}</div>
                            <Fenye/>
                      </div>
                   </div>
     }


}
