  import   React from "react"
  import  Lis from "./Lis"
  import  Userselect  from "./Userselect"
  import  OrderList  from "./Orderlist"

export  default  class Userlist extends  React.Component{
      constructor(props){
            super(props)
            this.state={
                  userlist:[],
                  timeStart:"",
                  timeEnd:"",
                  selectlis:[],
                  selectlis1:[],
                  nextpage:"1",
                  page:[],
                  page2:[],
                  page3:[],
                  pageShow:[],
                  prevpage:1

            }
            this.select=this.select.bind(this)
            this.showlis=this.showlis.bind(this)
            this.nextpage=this.nextpage.bind(this)
            this.prevpage=this.prevpage.bind(this)
      }
      componentDidMount(){
            fetch("http://pre.zhushang.net/Supplychain/getDataForHavePost",{
                   method:"post",
                   mode:"cors",
                   headers:{
                       "Content-type": "application/x-www-form-urlencoded"
                   },
                   body : "type=1&page=1&num=20"

            }).then (response =>{
                   return   response.json()
            }).then( (responseText)=>{
                  const arr1=responseText.slice(0,8)
                  const arr2=responseText.slice(10)
                  const arr=[...arr1,...arr2]
                  this.setState({userlist:arr , selectlis:arr})
            }  )
        }

      //  输入框子组件的日期1传递给父组件
      onchangeValue1(e){
         this.setState({timeStart:e.target.value})
      }
      // //  输入框子组件的日期2传递给父组件
      onchangeValue2(e){
         this.setState({timeEnd:e.target.value})   
      }
      // 按钮查询功能
      select(){
         const time1=  new Date(this.state.timeStart).getTime()
         const time2=  new Date(this.state.timeEnd).getTime()
         const arr=this.state.userlist
         console.log(arr)
         const newarr=arr.filter((elem)=>{
               const   litime= new Date(elem.create_time.slice(0,10)).getTime() 
               return   litime>=time1&&litime<=time2
         })
         console.log(newarr)
         this.setState({
                 pageShow:newarr,
                 selectlis:newarr,
                 nextpage:1 

          })
      }
      // 显示条数
      showlis(e){
          this.setState({prevpage:1}) 
        let pshow= Number(e.target.value)  
        let pcount=this.state.selectlis.length

        let arr=this.state.selectlis 
        console.log(arr,pcount,pshow)
      //   判断当前页面有几条数据
         if(pcount/pshow<=3&&pcount/pshow>2){
               const arr1=arr.slice(0,pshow)
               const arr2=arr.slice(pshow,2*pshow)
               const arr3=arr.slice(2*pshow)
               this.setState( {
                     nextpage:3,
                     page:arr1,
                     pageShow:arr1,
                     page2:arr2,
                     page3:arr3,
                           } )
         }else if(pcount/pshow>1&&pcount/pshow<=2){
               const arr4=arr.slice(0,pshow)
               const arr5=arr.slice(pshow) 
               this.setState( {
                     nextpage:2,
                     page:arr4,
                     pageShow:arr4,
                     page2:arr5
                         } )
         }else{
            const arr6=arr.slice(0,arr.lenth)
            this.setState( {
                   nextpage:1,
                   page:arr6,
                   pageShow:arr6,
                        } )

         }
      }
      // 下一页
      nextpage(){
       let prevpage=this.state.prevpage
       let  nextpage=this.state.nextpage
       const arrPage2=this.state.page2
       const arrPage3=this.state.page3
       if(prevpage<nextpage){
             prevpage++
             this.setState({prevpage:prevpage})
       }
       if(prevpage===3){
            this.setState({pageShow:arrPage3})
       }
       if(prevpage===2){
            this.setState({pageShow:arrPage2})
       }
      }
      // 上一页
      prevpage(){
         let prevpage=this.state.prevpage
         const arrPage1=this.state.page
         const arrPage2=this.state.page2
         const arrPage3=this.state.page3
         if(prevpage>1){
               prevpage--
               this.setState({prevpage:prevpage})
         }
         if(prevpage===1){
            this.setState({pageShow:arrPage1})
         }else if(prevpage===2){
            this.setState({pageShow:arrPage2})
         }else{
            this.setState({pageShow:arrPage3})  
         }
      }

      render(){

            const   arr=this.state.pageShow
            const  newarr=arr.map((elem ,i)=> {
                  return   <Lis  key={i} {...elem}></Lis>
            })

            return  <div id="tab" style={{ display:this.props.display}}> 
                        <div id="sale">
                              <select  id="selectmoney">
                                 <option value="总收入">总收入</option>
                                 <option value="后台">后台</option>
                                 </select>
                              <select name="" id="selectNumber" onChange={this.showlis}>
                                 <option>显示条数</option>
                                 <option value="6" >6</option>
                                 <option value="12" >12</option>
                                 <option value="18" >18</option>
                              </select>
                              {/* 兄弟组件传值 */}
                        <Userselect change1={this.onchangeValue1.bind(this)} change2={this.onchangeValue2.bind(this)} 
                                 value1={this.state.timeStart}   value2={this.state.timeEnd}/>
                                 <div id="buttonclick">
                                    <button id="select"  onClick={this.select}>查询</button>
                                    <button id="asyc">同步</button>
                                    <span className="text"> 交易上次手动同步时间:  <span id="time"> 2019-09-25 10:09:36 </span></span>
                                 </div>
                              {/* 表头组件 */}
                        <OrderList/>
                                 <div>{ newarr}</div>
                        <div id="fenye">
                                <div className="startpage"  onClick={this.prevpage}>上一页</div>
                                <div id="page"> 
                                <span id="start" >{this.state.prevpage}</span>/ <span id="endPage" >{this.state.nextpage}</span> 
                                </div>
                                <div className="endpage" onClick={this.nextpage}>下一页</div>
                                </div>    
                        </div>
                     </div>
       }


  }
