import React from 'react';
import ReactDOM from 'react-dom';
import './11.css';
import  Calcshow  from"./Calcshow"
import  Calccount  from"./Calccount"  
class Calc extends React.Component{
            constructor(props){
                super(props)
                this.state={

                }
            }
          render(){
                return  <div>
                       <Calcshow></Calcshow>
                       <Calccount  onClick={ this.solve}></Calccount>
                        </div>
          }

          solve(){

    // 获取表格标签 并为表格标签绑定事件函数 子元素通过冒泡共享父元素的函数
      const table=document.getElementById("calc")
      const show=document.getElementById("show")
      const animate=document.getElementById("animate")
      
  table.onclick=function(e){
      //   用e.target 代替this 获得目标元素
        let  td=e.target;
        console.log(show.innerHTML)
        if(td.nodeName==="TD"){
             switch(td.innerHTML){
                 case "AC": 
                   show.innerHTML=""
                   break;
                 case '.':
                     if(show.innerHTML.length!==0){
                          show.innerHTML+="."
                     }
                      break;
                  case "=":  
                      //  首先先加载动画  然后1.5s后 执行计算事件
                     
                        first().then(second)
                       
                  
                
                  break;
                  case "": 
                       e.stopPropagation()
                       break;
                  case "+/-": 
                  //   判断number  的类型  来显示 相应的值
                       let number=Number(show.innerHTML)
                       
                             if(number<0){
                                  show.innerHTML=Math.abs(number)
                             }else if(number>0){
                                    show.innerHTML="-"+show.innerHTML
                             }else if(number===0){
                                    show.innerHTML="0"
                             }else{
                                 show.innerHTML=show.innerHTML+"-"
                             }
                  break;
                  //    其他的td 
              default:  
                   
                  show.innerHTML+=td.innerHTML
             }
          

        } 

       
     }
//  定义一个计算函数
  function  calc(){
    let  result=""

      if( show.innerHTML!==""){
                       try{
                             if(show.innerHTML.indexOf("×")!==-1 ||show.innerHTML.indexOf("÷")!==-1 ){
                              //    乘法的时候  将显示屏中所有的乘号都替换成*
                              let str2=""
                              
                                 const str1=show.innerHTML.replace(/×/g,"*")
                                   str2=str1.replace(/÷/g,"/")
                                   show.innerHTML=str2
                             }
                             // eslint-disable-next-line
                           result=eval(show.innerHTML)
                            show.innerHTML=result.toFixed(2)
                       }catch(err){
                           show.innerHTML=err
                       }
                   }  
                   return  show.innerHTML
  }  
  // 
     function first(){
          return new Promise(  function(resolve){
              setTimeout(function(){
                  console.log(1)
                    animate.innerHTML=`
                    <div class="main">
  <div class="loadEffect">
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
              <span></span>
              <span></span>
          <span></span>
          <span></span>
          <span></span>
  </div>
</div>`
      console.log(animate.innerHTML)
          resolve()
              },1040)

          })
     }
     function second(){
          return new Promise(  function(resolve){
              setTimeout(function(){
                  console.log(2)
                    animate.innerHTML=""
                     show.innerHTML=calc() 
                    resolve()
              },2040)

          })
     }

     
}
      
      }

  
  ReactDOM.render(
    <Calc/>,
    document.getElementById('root')
  );
