import React from 'react';

import './11.css';
import  Calcshow  from"./calctor/Calcshow"



export  default  class Calc extends React.Component{
            constructor(props){
                super(props)
                this.state={
                    result:[]
                }
                this.showNumber=this.showNumber.bind(this)
                this.clearNumber=this.clearNumber.bind(this)
                this.comparyNumber= this.comparyNumber.bind(this)
                this.showResult=this.showResult.bind(this)
            }
          render(){
                return  <div>
                       <Calcshow show={this.state.result}></Calcshow>
                       <div id="calc">
                      <div id="methods">
                          <table id="table">
                               <tbody>
                                        <tr id="first">
                                            <td onClick={this.clearNumber}>AC</td>
                                            <td onClick={this.comparyNumber}>+/-</td>
                                            <td onClick={this.showNumber}>%</td>
                                            <td onClick={this.showNumber}>÷</td>
                                        </tr>
                                        <tr>
                                          <td onClick={this.showNumber}>7</td>
                                          <td onClick={this.showNumber}>8</td>
                                          <td onClick={this.showNumber}>9</td>
                                          <td onClick={this.showNumber}>×</td>
                                       </tr>
                                       <tr>
                                          <td onClick={this.showNumber}>4</td>
                                          <td onClick={this.showNumber}>5</td>
                                          <td onClick={this.showNumber}>6</td>
                                          <td onClick={this.showNumber}>-</td>
                                      </tr>
                                      <tr>
                                          <td onClick={this.showNumber}>1</td>
                                          <td onClick={this.showNumber}>2</td>
                                          <td onClick={this.showNumber}>3</td>
                                          <td onClick={this.showNumber}>+</td>
                                      </tr>
                               </tbody>
                               <tfoot>
                                      <tr>
                                         <td onClick={this.showNumber}>0</td>
                                         <td ></td>
                                         <td onClick={this.showNumber}>.</td>
                                         <td onClick={this.showResult}>=</td>
                                    </tr>
                               </tfoot>
                            </table>
                      </div>
              </div>
                      </div>
          }
    //  显示数字的
       showNumber(e){
            // this.state.result.push(e.target.value)
               const  newValue=e.target.innerHTML
              this.setState(  oldstate =>{
                return {
                   result: oldstate.result.concat(newValue),
                    }
           });
       } 
    // 加符号
    comparyNumber(){
         let  arrAdd=["-"]
        // 获得当前数组
         let arr=this.state.result 
        //  合并成一条字符串 
         let str=arr.join("")
        if(this.state.result.length===0){
            return 
        }
        // 判断当前字符串大于0
     // eslint-disable-next-line
          if(Number(str)>0&&Number(str)!==NaN){
            this.setState(  oldstate =>{
                return {
                   result: arrAdd.concat(oldstate.result),
                    }
           });
             } 
      // 如果当前字符串小于0  
       // eslint-disable-next-line
      if(Number(str)<0&&Number(str)!==NaN){
          const  arr=this.state.result
          const  newArr=arr.slice(1)
        this.setState(  oldstate =>{
            return {
               result: newArr,
                }
       });
         } 
    }  

    // 清除当前显示屏上所有的数字
      clearNumber(){
          const  arr=[]
          this.setState({
              result: arr
          })
      }
    //  显示结果
      showResult(){
        //  获得当前数组
        var  arr=this.state.result
         console.log(arr.join(""))   
         // eslint-disable-next-line
        var  str=  eval(arr.join(""))
        var  newArr=[].push(str)
        this.setState(  oldstate =>{
            return {
               result: newArr,
                }
       });
      }

}
