import   React from "react"
import  Lis from "./Lis"
import  Userselect  from "./Userselect"
import  OrderList  from "./Orderlist"
import   Fenye  from "./Fenye"
export  default  class Userlist extends  React.Component{

  constructor(props){
         super(props)
         this.state={
               userlist:[],
               timeStart:"",
               timeEnd:""
         }
  }
    componentDidMount(){
          fetch("http://pre.zhushang.net/Supplychain/getDataForHavePost",{
                 method:"post",
                 mode:"cors",
                 headers:{
                     "Content-type": "application/x-www-form-urlencoded"
                 },
                 body : "type=1&page=1&num=20"

          }).then (response =>{
                 return   response.json()
          }).then( (responseText)=>{
                  this.setState({userlist:responseText})
          }  )
      }

    //   父组件传递给子组件的函数 


     render(){  
             console.log(this.state.userlist)
              const arr1=this.state.userlist.slice(0,8)
              const arr2=this.state.userlist.slice(10)
              const arr=[...arr1,...arr2]
             const  newarr=arr.map((elem ,i)=> {
                   return   <Lis  key={i} {...elem}></Lis>
             })
            return  <div id="tab"> 
                        <div id="sale">
                      <Userselect/>
                      <OrderList/>
                      <div>{newarr}</div>
                      <Fenye/>
                      </div>
                   </div>
     }


}
