import React from 'react';
import  Calcshow  from"./Calcshow"
import  Animate from "./animate"


export  default  class Calc extends React.Component{
            constructor(props){
                super(props)
                this.state={
                    result:[],
                    display:"none"

                 }
                this.showNumber=this.showNumber.bind(this)
                this.clearNumber=this.clearNumber.bind(this)
                this.comparyNumber= this.comparyNumber.bind(this)
                this.showResult=this.showResult.bind(this)
                this.showZero=this.showZero.bind(this)
                this.showCircle=this.showCircle.bind(this)
            }
          render(){
                return   <div style={{ display:this.props.display}} >
                         <div id="tab">
                                <div  id="index">
                                    {/*  显示组件 */}
                                <Calcshow show={this.state.result}></Calcshow>
                                <div id="calc">
                                <div id="methods">
                                    <table id="table">
                                        <tbody>
                                                <tr id="first">
                                                        <td onClick={this.clearNumber}>AC</td>
                                                        <td onClick={this.comparyNumber}>+/-</td>
                                                        <td onClick={this.showNumber}>%</td>
                                                        <td onClick={this.showNumber}>÷</td>
                                                </tr>
                                                <tr>
                                                    <td onClick={this.showNumber}>7</td>
                                                    <td onClick={this.showNumber}>8</td>
                                                    <td onClick={this.showNumber}>9</td>
                                                    <td onClick={this.showNumber}>×</td>
                                                </tr>
                                                <tr>
                                                    <td onClick={this.showNumber}>4</td>
                                                    <td onClick={this.showNumber}>5</td>
                                                    <td onClick={this.showNumber}>6</td>
                                                    <td onClick={this.showNumber}>-</td>
                                                </tr>
                                                <tr>
                                                    <td onClick={this.showNumber}>1</td>
                                                    <td onClick={this.showNumber}>2</td>
                                                    <td onClick={this.showNumber}>3</td>
                                                    <td onClick={this.showNumber}>+</td>
                                                </tr>
                                        </tbody>
                                        <tfoot>
                                                <tr>
                                                    <td onClick={this.showNumber}>0</td>
                                                    <td ></td>
                                                    <td onClick={this.showNumber}>.</td>
                                                    <td onClick={this.showResult}>=</td>
                                                </tr>
                                        </tfoot>
                                        </table>
                                        {/* 加载动画组件 */}
                                        <Animate display={this.state.display} />
                                </div>
                        </div>
                                </div>
                       </div>
                </div>
          }
       // 显示数字的
       showNumber(e){
            // this.state.result.push(e.target.value)
               const  newValue=e.target.innerHTML
              this.setState(  oldstate =>{
                return {
                   result: oldstate.result.concat(newValue),
                    }
           });
       } 
    // 加符号
    comparyNumber(){
            let  arrAdd=["-"]
            // 获得当前数组
            let arr=this.state.result 
            //  合并成一条字符串 
            let str=arr.join("")
            if(this.state.result.length===0){
                return 
            }
            // 判断当前字符串大于0
            // eslint-disable-next-line
            if(Number(str)>0&&Number(str)!==NaN){
                this.setState(  oldstate =>{
                    return {
                    result: arrAdd.concat(oldstate.result),
                        }
            });
                } 
            // 如果当前字符串小于0  
            // eslint-disable-next-line
            if(Number(str)<0&&Number(str)!==NaN){
                const  arr=this.state.result
                const  newArr=arr.slice(1)
                this.setState(  oldstate =>{
                    return {
                    result: newArr,
                        }
            });
                } 
        }  

      // 清除当前显示屏上所有的数字
    clearNumber(){
          const  arr=[]
          this.setState({
              result: arr
          })
      }
      //  显示结果
    showResult(){
        this.setState({display:"block"})
          //  获得当前数组
        let  arr=this.state.result
        let  str=arr.join("") 
            //  判断当前字符串中有没有 乘号和除号  有乘号和除号全部替换成 对应的符号
        try {
            if(str.indexOf("×")!==-1 ||str.indexOf("÷")!==-1 ){
                //    乘法的时候  将显示屏中所有的乘号都替换成*
                     const str1=str.replace(/×/g,"*")
                      str=str1.replace(/÷/g,"/")

                   }
         // eslint-disable-next-line
        let  str3=eval(str)
        let  newArr=[str3]
        setTimeout(()=>{
          this.setState(  {
            result:newArr,
            display:"none"
        });
        },1500)

      }
         catch (error) {
             const  newArr=[].push(error)
             setTimeout(()=>{
                 this.setState(  {
                result:newArr
            });
             },1500)

        }
    }
    //   对于0的处理
