import  {SHOW_NUMBER,COMPARY_NUMBER}  from  "./actionTypes"
const  defaultState={
     numberConcat:""
}
export  default(state=defaultState,action)=>{
     // eslint-disable-next-line 
    switch(action.type){
        case SHOW_NUMBER: 

             let  newState=JSON.parse(JSON.stringify(state))
             newState.numberConcat+=action.show
             return  newState

        case COMPARY_NUMBER:  

            // 判断字符串是不是数字  如果是数字大于0 和小于0  
            if(newState.numberConcat.length===0){
                return 
            }
             // eslint-disable-next-line
            if(Number(newState.numberConcat)>0&&Number(newState.numberConcat)!==NaN){
                let  newState=JSON.parse(JSON.stringify(state))
                newState.numberConcat="-"+ newState.numberConcat
                return  newState
            }
            // eslint-disable-next-line
            if(Number(newState.numberConcat)<0&&Number(newState.numberConcat)!==NaN){
                let  newState=JSON.parse(JSON.stringify(state))
                newState.numberConcat=newState.numberConcat.slice(1)
                return  newState
            }
        }
    return  state
}
