import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// 按钮组件

class Square extends React.Component {
       
    constructor(props){
          super(props)
          this.arr=["1","2","3"]
    }
    render() {
      return (
        <button className="square">
             {}
        </button>
      );
    }
}
  
  class Board extends React.Component {
   
  
    render() {
      const status = 'Next player: X';
  
      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
              <Square></Square>
              <Square></Square>
              <Square></Square>
              <Square></Square>
          </div>
          <div className="board-row">
              <Square></Square>
              <Square></Square>
              <Square></Square>
              <Square></Square>
          </div>
          <div className="board-row">
              <Square></Square>
              <Square></Square>
              <Square></Square>
              <Square></Square>
          </div>
        </div>
      );
    }
  }
  
  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }
  
  // ========================================
  
  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
