import React  from "react" 
import {BrowserRouter as Router,Route,Link} from "react-router-dom"
import {connect} from "react-redux"
import {indexAction,userAction} from "../store/actionCreators"
import Calc from "../calctor/index.js"
import Userlist from "../userlist/index.js"
function  AppRouter(){
    const {display1,display2,indexAction,userAction}=this.props

    return(
        <Router>
             <div className="lists">
                    <div></div>
                    {/* // eslint-disable-next-line */}
                    <div onClick={indexAction} className={display1==="block"?"blue":"index1"}>
                        <Link to="/block">首页</Link>
                    </div>
                    <div></div>
                    {/* // eslint-disable-next-line */}
                    <div onClick={userAction} className={display2==="block"?"blue":"sale1"}>
                        <Link to="/list/block">收入分析</Link>
                    </div>
                    <div></div>
                </div>
            {/* <ul>
                <li> <Link to="/">首页</Link></li>
                <li> <Link to="/list/123">列表</Link></li>
            </ul> */}
            {/* exact 表示精确匹配 */}
            <Route path="/:display" exact component={Calc}/>
            {/*:id 表示规则 */}
            <Route path="/list/:display"  component={Userlist }/>
        </Router>
    )
}
const stateToProps=(state)=>{
    return{
        display1:state.display1,
        display2:state.display2
    }
}
const dispatchToProps={
    indexAction,
    userAction
}
export default connect(stateToProps,dispatchToProps)(AppRouter)
