import {USERLIST,SELECTLIST,SHOWDATE1,SHOWDATE2,SELECTDATA,INITIALIZE,PAGE1,PAGE2,page3} from "./actionTypes"
const defaultState={
    userlis:[],
    dateValue1:"",
    dateValue2:"",
    prevpage:"1",
    nextpage:"1",
    selectlis:[]
}
export default (state=defaultState,action)=>{
    if(action.type===USERLIST){
        const newState=JSON.parse(JSON.stringify(state))
        newState.userlis=action.value
        return newState
    }
    if(action.type===SHOWDATE1){
        const newState=JSON.parse(JSON.stringify(state))
        newState.dateValue1=action.value
        return newState
    }
    if(action.type===SHOWDATE2){
        const newState=JSON.parse(JSON.stringify(state))
        newState.dateValue2=action.value
        return newState
    }
    if(action.type===SELECTLIST){
        const newState=JSON.parse(JSON.stringify(state))
        newState.selectlis=action.value
        return newState
    }
    if(action.type===INITIALIZE){
        const newState=JSON.parse(JSON.stringify(state))
        newState.prevpage="1"
        return newState
    }
    return state
}
