import React from 'react';
import CalcShow from "./calcShow"
import Animate from "./animate"
import {showNumberAction} from "../store/actionCreators"
import {connect} from "react-redux"

class Calctor extends React.Component{
    constructor(props){
        super(props)
        this.state={
            display:"none"
        }

        this.clearNumber=this.clearNumber.bind(this)
        this.comparyNumber= this.comparyNumber.bind(this)
        this.showResult=this.showResult.bind(this)
        this.showZero=this.showZero.bind(this)
        this.showCircle=this.showCircle.bind(this)
    }
    render(){
        const { showNumberAction ,show}=this.props
        //判断动画组件是否加载   
        let jsx=this.state.display==="block"?<Animate/>:""
        return(
            <div style={{display:this.props.display}}>
                <div className="tab">
                    <div className="index">
                        {/*  显示组件 */}
                        <CalcShow show={show}></CalcShow>
                            <div className="calc">
                                <div className="methods">
                                    <table className="table">
                                        <tbody>
                                            <tr className="first">
                                                <td onClick={this.clearNumber}>AC</td>
                                                <td onClick={this.comparyNumber}>+/-</td>
                                                <td onClick={showNumberAction}>%</td>
                                                <td onClick={showNumberAction}>÷</td>
                                            </tr>
                                            <tr>
                                                <td onClick={showNumberAction}>7</td>
                                                <td onClick={showNumberAction}>8</td>
                                                <td onClick={showNumberAction}>9</td>
                                                <td onClick={showNumberAction}>×</td>
                                            </tr>
                                            <tr>
                                                <td onClick={showNumberAction}>4</td>
                                                <td onClick={showNumberAction}>5</td>
                                                <td onClick={showNumberAction}>6</td>
                                                <td onClick={showNumberAction}>-</td>
                                            </tr>
                                            <tr>
                                                <td onClick={showNumberAction}>1</td>
                                                <td onClick={showNumberAction}>2</td>
                                                <td onClick={showNumberAction}>3</td>
                                                <td onClick={showNumberAction}>+</td>
                                            </tr>
                                        </tbody>
                                        <tfoot>
                                            <tr>
                                                <td onClick={this.showZero}>0</td>
                                                <td ></td>
                                                <td onClick={this.showCircle}>.</td>
                                                <td onClick={this.showResult}>=</td>
                                            </tr>
                                        </tfoot>
                                    </table>
                                    {/* 是否加载动画组件 */}
                                    {jsx}
                                </div>
                            </div>
                    </div>
                </div>
            </div>
        )        
    }

    // 加负号
    comparyNumber(){
        let arrAdd=["-"]
        // 获得当前数组
        let arr=this.state.result 
        //  合并成一条字符串 
        let str=arr.join("")
        if(this.state.result.length===0){
            return 
        }
        // 判断当前字符串大于0
        // eslint-disable-next-line
        if(Number(str)>0&&Number(str)!==NaN){
            this.setState(oldstate=>{return{result:arrAdd.concat(oldstate.result)}})
        } 
        // 如果当前字符串小于0  
        // eslint-disable-next-line
        if(Number(str)<0&&Number(str)!==NaN){
            const arr=this.state.result
            const newArr=arr.slice(1)
            this.setState(oldstate=>{return{result: newArr}});
        } 
    }  

    //清除当前显示屏上所有的数字
    clearNumber(){
        const arr=[]
        this.setState({result: arr})
    }
    //显示结果
    showResult(){
        this.setState({display:"block"})
        //获得当前数组
        let arr=this.state.result
        let str=arr.join("") 
        //判断当前字符串中有没有 乘号和除号  有乘号和除号全部替换成 对应的符号
        try{
            if(str.indexOf("×")!==-1 ||str.indexOf("÷")!==-1){
                //乘法的时候将显示屏中所有的乘号都替换成*
                const str1=str.replace(/×/g,"*")
                str=str1.replace(/÷/g,"/")
            }
            //eslint-disable-next-line
            let str3=eval(str)
            let newArr=[str3]
            setTimeout(()=>{this.setState({result:newArr,display:"none"})},1500)

        }
        catch (error) {
            const newArr=[].push(error)
            setTimeout(()=>{
                this.setState({result:newArr})
            },1500)
        }
    }
    //对于0的处理
    showZero(e){
        let arr=this.state.result
        const newValue=e.target.innerHTML
        if(arr[0]!=="0"){
            this.setState(oldstate=>{return{result: oldstate.result.concat(newValue)}})
        }
    }
    //对于点的处理
    showCircle(e){
        let arr=this.state.result
        const newValue=e.target.innerHTML
        if(arr[0]!=="."&&arr[arr.length-1]!=="."){
            this.setState(oldstate =>{
            return{result:oldstate.result.concat(newValue)}})
        }
    }
}

const stateToProps=(state)=>{
    return {
        show:state.shownumber
    }
}
const dispatchToProps={
    showNumberAction
}

export default connect(stateToProps,dispatchToProps)(Calctor)
